class OrganPrinter:
    def __init__(self):
        self.safety_test_mode = False
        self.bio_inks = {'ink1': 50, 'ink2': 70, 'ink3': 30}  # dosage in milliliters

    def load_inks(self):
        print('Loading bio-inks:', self.bio_inks)

    def start_printing(self):
        if self.safety_test_mode:
            print('Running in safety test mode. Step-by-step analysis is enabled.')
        else:
            print('Starting organ printing in clinical mode.')

    def adjust_inks(self, ink_modifications):
        for ink, mod in ink_modifications.items():
            self.bio_inks[ink] = mod
        print('Adjusted bio-inks:', self.bio_inks)

printer = OrganPrinter()
printer.load_inks()
printer.start_printing()

